import React from 'react'
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";


class Book extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            name:'',
            email:'',
            message:''
        }
    }
  state = {
    startDate: new Date()
  };

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  render() {
    return (
        <div className="container">
        <br></br>
            <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                <div className="form-group">
                    <label htmlFor="fname">First Name:</label>
                    <input type="text" className="form-control" value={this.state.fname} onChange={this.onNameChange.bind(this)}/>
                </div>

                <div className="form-group">
                    <label htmlFor="lname">Last Name:</label>
                    <input type="text" className="form-control" value={this.state.lname} onChange={this.onNameChange.bind(this)}/>
                </div>
            </form>

            <h4>Please Choose a Date:</h4>
            <DatePicker
                selected={this.state.startDate}
                onChange={this.handleChange}
            />
      </div>
    );
  }
handleSubmit(event) {
}
onNameChange(event) {
	this.setState({name: event.target.value})
}

}

export default Book;
