import React from 'react'
import { Container, Row, Col } from 'reactstrap';
import { Icon } from 'semantic-ui-react';
import { Button } from 'react-bootstrap';
import history from './history';
import {Link, NavLink} from 'react-router-dom'

const list1 = [
    {
      id: 'a',
      firstname: 'Robin',
      url: 'https://www.vets4pets.com/siteassets/species/cat/close-up-of-cat-looking-up.jpg?w=585&scale=down',
      type:'cat',
    },
    {
      id: 'b',
      firstname: 'Dave',
      url: 'https://www.loveyourdog.com/wp-content/uploads/2019/05/Best-Crates-For-Golden-Retrievers-900x500.jpg',
      type:'dog',
    },
  ];
  const list2 = [
    {
      id: 'c',
      firstname: 'Mary',
      url: 'https://specials-images.forbesimg.com/imageserve/5de6f2d8c283810006a3947f/960x0.jpg?fit=scale',
      type:'cat',
    },
    {
      id: 'd',
      firstname: 'Dodo',
      url: 'https://www.thesprucepets.com/thmb/sfuyyLvyUx636_Oq3Fw5_mt-PIc=/3760x2820/smart/filters:no_upscale()/adorable-white-pomeranian-puppy-spitz-921029690-5c8be25d46e0fb000172effe.jpg',
      type:'dog',
    },
  ];
  const list12 = list1.concat(list2);
  const list3 = [
    {
      id: 'e',
      firstname: 'Amy',
      url: 'https://newsd.co/wp-content/uploads/2018/02/cocker-spaniel-names.jpg',
      type:'dog',
    },
    {
      id: 'f',
      firstname: 'Yoshi',
      url: 'https://i2.wp.com/www.paws.org/wp-content/uploads/2019/10/Alphie_cat_29864612_101115_profile_MB-2.jpg?fit=1024%2C583&ssl=1',
      type:'cat',
    },
  ];
  const pets = list12.concat(list3);


const Adoption = (props) => {
    state = {
        search:""
    }

    allPets = pets =>{
        const {search} = this.state;
        var code = pets.code.toLowerCase()
        if(search !== ""&& pets.type.indexOf(search) === -1){
            return null
        }
    }
    return(
        <Container>
      <Row>
        <Col><div className="container">
            <h4 className="left">Adoption</h4>
            <br></br>
            <br></br>
            <br></br>
            <ul>
                {list1.map(item => (
                <li key={item.id}>
                    <div><img width="250px" height="250px" src={item.url}/></div>
                    <div>{item.firstname}</div>
                    <div><button><Link to="/book">Interested in Adoption</Link></button></div>
                    <br></br>
                </li>
                ))}
            </ul>
        </div>
        </Col>
        <Col><div className="container">
            <br></br>
            <br></br>
            <br></br>
            <ul>
                {list2.map(item2 => (
                <li key={item2.id}>
                    <div><img width="250px" height="250px" src={item2.url}/></div>
                    <div>{item2.firstname}</div>
                    <div><button><Link to="/book">Interested in Adoption</Link></button></div>
                    <br></br>
                </li>
                ))}
            </ul>
        </div></Col>
        <Col><div className="container">
            <br></br>
            <br></br>
            <br></br>
            <ul>
                {list3.map(item3 => (
                <li key={item3.id}>
                    <div><img width="250px" height="250px" src={item3.url}/></div>
                    <div>{item3.firstname}</div>
                    <div><button><Link to="/book">Interested in Adoption</Link></button></div>

                    <br></br>
                </li>
                ))}
            </ul>
        </div></Col>
      </Row>
    </Container>
    )
}

export default Adoption;
